function SortOutCords() {
    if (temp_BountyCords != 0) {
        temp_BountyCords.splice(0, temp_BountyCords.length);
    }
    var stringLenth = 0;
    var indexNumber = 0;
    var firstElement = "";
    var lat = 0;
    var lng = 0;
    var latlng;
    while (tempPath.length > 6) {
        stringLenth = tempPath.length;
        indexNumber = tempPath.indexOf("LatLng", 2);
        tempPath = tempPath.substr(indexNumber, stringLenth);
        firstElement = tempPath.substr(0, indexNumber);
        if (firstElement != "") {
            stringLenth = firstElement.length;
            indexNumber = firstElement.indexOf("(");
            firstElement = firstElement.substr(indexNumber + 1, stringLenth);
            indexNumber = firstElement.indexOf(",");
            lat = firstElement.substr(0, indexNumber);
            firstElement = firstElement.substr(indexNumber + 1, stringLenth);
            indexNumber = firstElement.indexOf(")");
            lng = firstElement.substr(0, indexNumber);
            latlng = { lat: lat, lng: lng }
            temp_BountyCords.push(latlng);
        }
    };
}




